import React, { useState } from "react";
import Altitude from "./components/Altitude";
import HIS from "./components/HIS";
import ADI from "./components/ADI";
import VisualDisplay from "./components/VisualDisplay";
import InputPopup from "./components/InputPopup";
import "./App.css";

function App() {
  const [mode, setMode] = useState("text");
  const [showPopup, setShowPopup] = useState(false);

  return (
    <div className="app">
      <h1>Flight Monitor</h1>
      <div className="top-buttons">
        <button className={mode === "text" ? "active" : ""} onClick={() => setMode("text")}>TEXT</button>
        <button className={mode === "visual" ? "active" : ""} onClick={() => setMode("visual")}>VISUAL</button>
        <button onClick={() => setShowPopup(true)}>+</button>
      </div>

      {mode === "text" && (
        <div className="sensor-container">
          <Altitude />
          <HIS />
          <ADI />
        </div>
      )}

      {mode === "visual" && <VisualDisplay />}

      {showPopup && <InputPopup onClose={() => setShowPopup(false)} />}
    </div>
  );
}

export default App;
