// client/src/components/InputPanel.js
import React, { useState } from "react";
import { updateSensor } from "../api/sensors";
import "./InputPanel.css";

export default function InputPanel({ altitude, his, adi, setAltitude, setHis, setAdi, onClose }) {
  const [altInput, setAltInput] = useState(altitude);
  const [hisInput, setHisInput] = useState(his);
  const [adiInput, setAdiInput] = useState(adi);

  const handleSend = () => {
    updateSensor("altitude", altInput);
    updateSensor("his", hisInput);
    updateSensor("adi", adiInput);
    setAltitude(altInput);
    setHis(hisInput);
    setAdi(adiInput);
    onClose();
  };

  return (
    <div className="panel-overlay">
      <div className="panel-box">
        <div className="row">
          <label>Altitude</label>
          <input type="number" value={altInput} onChange={(e) => setAltInput(Number(e.target.value))} />
        </div>
        <div className="row">
          <label>HIS</label>
          <input type="number" value={hisInput} onChange={(e) => setHisInput(Number(e.target.value))} />
        </div>
        <div className="row">
          <label>ADI</label>
          <input type="number" value={adiInput} onChange={(e) => setAdiInput(Number(e.target.value))} />
        </div>
        <div className="buttons">
          <button onClick={handleSend}>SEND</button>
          <button onClick={onClose}>CLOSE</button>
        </div>
      </div>
    </div>
  );
}
