// client/src/components/InputPanel.js
import React, { useState } from "react";
import { updateSensor } from "../api/sensors";
import "./InputPanel.css";

export default function InputPanel({
  altitude,
  his,
  adi,
  setAltitude,
  setHis,
  setAdi,
  onClose
}) {
  const [newAltitude, setNewAltitude] = useState(altitude);
  const [newHis, setNewHis] = useState(his);
  const [newAdi, setNewAdi] = useState(adi);

  const handleSend = () => {
    updateSensor("altitude", newAltitude);
    updateSensor("his", newHis);
    updateSensor("adi", newAdi);
    setAltitude(newAltitude);
    setHis(newHis);
    setAdi(newAdi);
    onClose(); // Close the panel
  };

  return (
    <div className="input-panel">
      <div className="input-row">
        <label>Altitude</label>
        <input
          type="number"
          value={newAltitude}
          onChange={(e) => setNewAltitude(Number(e.target.value))}
        />
      </div>
      <div className="input-row">
        <label>HIS</label>
        <input
          type="number"
          value={newHis}
          onChange={(e) => setNewHis(Number(e.target.value))}
        />
      </div>
      <div className="input-row">
        <label>ADI</label>
        <input
          type="number"
          value={newAdi}
          onChange={(e) => setNewAdi(Number(e.target.value))}
        />
      </div>
      <button className="send-btn" onClick={handleSend}>
        SEND
      </button>
    </div>
  );
}
